import './App.css';
import DessertsList from './DessertList'
import {useState} from 'react'

const ToDos = props =>(
    <tr>
        <td>
            <label>{props.id}</label>
        </td>
        <td>
            <input />
        </td>
        <td>
            <label>{props.createdAt}</label>
        </td>

    </tr>
);

const topDesserts = [
    {
        id: 1,
        title: "Tiramisu",
        price:"$7.00",
    },
    {
        id: 2,
        title: "Cake",
        price:"$3.00",
    },
    {
        id: 3,
        title: "Chocolate Mousse",
        price:"$5.00",
    },
    {
    id: 4,
    title: "Red Velvet Cake",
    price:"$4.00",
},
];

function App() {
    const [todos,setToDos] = useState([{
        id:"todo 1",
        createdAt:"18:00",
    },
    {
        id:"todo 2",
        createdAt:"14:20",
    },
    {
        id:"todo 3",
        createdAt:"05:40",
    }

    ]);

    const reverseOrder = () => {
        setToDos([...todos].reverse())
    }

  return (
    <div>
        <button OnClick={reverseOrder}>Reverse</button>
        <h2>To Dos</h2>
        <table>
            todos.map((todo,index) => {
                <ToDos id={todo.id} createdAt={todo.createdAt} />
            });
        </table>

        <h2>List of low calorie desserts:</h2>
        <DessertsList data={topDesserts}/>
    </div>
  );
}

export default App;
