import {useState} from 'react'
import {AiFillStar} from 'react-icons/ai'

function FormRange() {
  const [score, setScore] = useState("10");
  const [comment, setComment] = useState("");
  
  const handleSubmit = (e) => {
      e.preventDefault();
      if(Number(score) <= 5 && comment.length <= 10){
          alert("you need to leave a comment");
          return;
      }
      console.log(score);
      console.log(comment);
      setComment("");
      setScore("10");
      
  }
  
  return <form onSubmit={handleSubmit}>
            <fieldset>
                <div className="field">
                    <label htmlFor="score">Score: {score} <AiFillStar /></label>
                    <input id="score" type="range" min="1" max="10" name="score" value={score} onChange={(e) => setScore(e.target.value)} />
                    <div>
                    <textarea id="comment" type="text" placeholder="Write a Comment" name="comment" value={comment} onChange={(e) => setComment(e.target.value)} />
                    </div>
                </div>

                <button type="submit">Submit</button>
            </fieldset>
        </form>
}

export default FormRange;
