import "./Styles.css";
import { useTheme } from "../ThemeContext";

const Switch = () => {
  const { theme } = useTheme();
  return (
    <label className="switch">
      <input
        type="checkbox"
        checked={theme === "light"}
        onChange={useTheme.toggleSwich()}
      />
      <span className="slider round" />
    </label>
  );
};

export default Switch;
